import React,{useState} from 'react'



export const  TodoForm =({title,description,handleTask,idTaskUpdate})=>{

     const showBtnUpdate = idTaskUpdate ? <button>
         mettre a jour
     </button>
     :
     <button>
         Enregistrer
     </button>
    return(
        <tr>
        <td>
        <input onChange={handleTask} type="text" placeholder="title" id="title" value={title || ''} />
        </td>
        <td>
        <input onChange={handleTask} type="text" placeholder="title" id="description" value={description || '' } />
        </td>
        <td>
        {
            showBtnUpdate
        }
        </td>
        </tr>
    )
}

const TodoEditor = ({addTodo,idTaskUpdate}) => {
    let data = {
        title: '',
        description: ''
    }

    const [task, setTask] = useState(data)

    const handleTask = e=>{
        setTask({...task,[e.target.id]: e.target.value})
    }
    const submitTask=e=>{
        e.preventDefault()
      addTodo(task)

    }




    const { title,description } = task;
    return (

        <div>

            <form onSubmit={idTaskUpdate ? updateTodo : submitTask}>
            <table>
                <TodoForm title={title} description={description} handleTask={handleTask} />

                </table>
            </form>


        </div>
    )
}

export default TodoEditor
