import React,{useState} from 'react'
import { connect } from 'react-redux'
import { addTodo } from '../../redux'
import ITodo from '../../ITodo';

const TodoEditor = (props) => {
  const data = {
    title: ITodo.title,
    description: ITodo.description
  }
  const {listTask,addTodo} = props;
  const [todo, setTodo] = useState(data)
  const handleChange = e=>{
    setTodo({...todo,[e.target.id]: e.target.value})
  }

  const handleSubmit = (e)=>{

    const { title,description } = todo
    e.preventDefault();
    if(title && description){
      addTodo(todo);
    }else{
      alert("champ vide")
    }
    
     setTodo(data);
  }
  const { title,description } = todo
  return (
    <div>
      <input 
      id="title"
      type="text" 
      value={title || ''} 
      placeholder="Title " 
      onChange={handleChange}
      />
      <input 
      id="description"
      type="text" 
      value={description || ''} 
      placeholder="Qu'est ce que vous voulez faire ? " 
      onChange={handleChange}
      />
      <button id="bAdd" onClick={handleSubmit}>ADD</button>
    </div>
  )
}

const mapStateToProps = state=>{
  return{
    listTask: state.listTask
  }
}
const mapDispatchToProps = dispatch=>{
  return{
    addTodo: (todo)=>dispatch(addTodo(todo))
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(TodoEditor)
