import React,{useState} from 'react'
import { useSelector,useDispatch } from 'react-redux';
import { deleteTodo,updateTodo } from '../../redux';

const TodoList = () => {
  
  const listTask = useSelector( state=>state.listTask)
  const dispatch = useDispatch()
  const [taskUpdate, setTaskUpdate] = useState({})


  const [update, setUpdate] = useState('')
  const showInputUpdate  = (id,task,title)=>{
    setUpdate(id)
    const data = {
      title,
      task
    }
    
    setTaskUpdate(data)
    
  }
 
  const hanldeUpdate = (id)=>{
    
    dispatch(updateTodo(taskUpdate,id))
    setUpdate('')
    setTaskUpdate('')
  }

const handleChange = (e)=>{
  setTaskUpdate({...taskUpdate,[e.target.id]:e.target.value })
}
 
const { title,task } = taskUpdate;
  
  const displayTask = listTask.map(task=>(
    <tr key={task.id}>
      <td>{task.id}</td>

      {
        update == task.id
        ?
        (

          <>

          <td>
            <input 
            id="title"
            type='text' 
            value={title}  
            onChange={(e)=>setTaskUpdate(e.target.value)}
            />
          </td>
          <td>
            <input 
            id="description"
            type='text' 
            value={task.task}  
            onChange={(e)=>setTaskUpdate(e.target.value)}
            />
          </td>
          </>
        )
        :
        (
          <>
            <td>{task.task}</td>
          <td>{task.task}</td>
          </>
        )
      }
      <td>
      {
        update == task.id
        ? 
        (<button onClick={()=>{hanldeUpdate(task.id)}}>
          Save
        </button>)
        :
        (
        <button onClick={()=>showInputUpdate(task.id,task.task,task.title)}>
          Update
        </button>
        )
      }
      </td>
      <td>
        <button onClick={()=>dispatch(deleteTodo(task.id))}>
          Delete
        </button>
      </td>
    </tr>
  ))
  return (
    <div>
      <hr />
      <h3>
        <u>List Task</u>
      </h3>
      <div id='mainTable'>
        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Title</th>
              <th>Todo</th>
              <th>Update</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
          {displayTask}
          </tbody>
        </table>
        
      
      </div>
    </div>
  )
}

export default TodoList
