import React,{useState} from 'react'
import TodoEditor from './components/TodoEditor'
import TodoList from './components/TodoList'

const App = () => {
    const data = [
        {
            title: 'Manger',
            description: "Mange du gateau"
        },
        {
            title: 'Manger',
            description: "Mange du gateau"
        }
    ]
    const [todos, setTodos] = useState(data)
    const [idTaskUpdate, setIdTaskUpdate] = useState(null)
    const addTodo = ({title,description})=>{
        setTodos([...todos,{
            title,
            description,
        }])

    }


    const updateTodo = (indexU,task)=>{
        const todoUpadated = todos.map((todo,index)=>( index == indexU ? { title: task.title,description: task.description} : todo ))
        setTodos(todoUpadated)
    }
    return (
        <div>
            <TodoEditor addTodo={addTodo}  />
            <TodoList todos={todos} idTaskUpdate={idTaskUpdate} />
        </div>
    )
}

export default App
