import { 
  ADD_TODO,
  DELETE_TODO,
  UPDATE_TODO
 } from './taskTypes';

export const addTodo = (todo) =>{
  return{
    type: ADD_TODO,
    payload: {
      title: todo.title,
      task: todo.description,
      id: 1
    }
  }
}

export const deleteTodo = id=>{
  return {
    type: DELETE_TODO,
    payload: id
  }
}

export const updateTodo = (newTask,id) =>{
  console.log(newTask)
  return{
    type: UPDATE_TODO,
    payload: {
      task: "aaaaaa",
      title: "bbbbbbbbbbb",
      id: id
    }
  }
}
