import React,{useState} from 'react'
import { TodoForm } from '../TodoEditor'






const TodoList = ({todos,idTaskUpdate,setIdTaskUpdate,updateTodo}) => {

    const [goUpdate, setGoUpdate] = useState(false)





    const showFormUpdate = (id)=>{
        setIdTaskUpdate(id)
    }

    const handleUpdate = (e,index,task)=>{
        //e.preventDefault();
        //updateTodo(index,task)c
        console.log("update")
    }


    const displayTodo = todos.map((todo,index)=>(
        index == idTaskUpdate ? 
        (<form onSubmit={(e)=>handleUpdate(e)}>
            <TodoForm  title={todo.title} description={todo.description} idTaskUpdate={idTaskUpdate} />
        </form>)
         : (
            <tr key={index} onClick={()=>showFormUpdate(index)}>
            <td>
                <input type="checkbox" name="" id=""/>
            </td>
            <td>
                {todo.title}
            </td>
            <td>
                {todo.description}
            </td>
            <td>
                <button>
                    delete
                </button>
            </td>
        </tr>
        )
    ))
    return (
        <div>

            <table>
                <thead>
                    <tr>
                        <th>Check</th>
                        <th>Title</th>
                        <th>Todo</th>
                        <th>Del</th>
                    </tr>
                </thead>
                <tbody>
                    {displayTodo}
                </tbody>
            </table>
        </div>
    )
}

export default TodoList
