import { 
  ADD_TODO,
  DELETE_TODO,
  UPDATE_TODO
 } from './taskTypes';

export const addTodo = (todo) =>{
  console.log(todo,'reducer')
  return{
    type: ADD_TODO,
    payload: {
      title: todo.title,
      task: todo.description,
      id: 1
    }
  }
}

export const deleteTodo = id=>{
  return {
    type: DELETE_TODO,
    payload: id
  }
}

export const updateTodo = ({title,task},id) =>{

  return{
    type: UPDATE_TODO,
    payload: {
      task: task,
      title: title,
      id: id
    }
  }
}
