import React,{useState} from 'react'
import { useSelector,useDispatch } from 'react-redux';
import { deleteTodo,updateTodo,updateCheck } from '../../redux';
import ITodo from '../../ITodo';

const TodoList = () => {
  
  const listTask = useSelector( state=>state.listTask)
  const dispatch = useDispatch()
  const data = {
    title: ITodo.title,
    task: ITodo.description,
    isCompleted: null
  }
  const [taskUpdate, setTaskUpdate] = useState(data)
  const [checkCompleted, setCheckCompleted] = useState(null)


  const [update, setUpdate] = useState('')
  const showInputUpdate  = (id,task,title,isCompleted)=>{
    setUpdate(id)
    data.title = title
    data.task =task
    data.isCompleted = isCompleted
    
    setTaskUpdate(data)
    
  }
 
  const hanldeUpdate = (id)=>{
    
    console.log(checkCompleted)
    //dispatch(updateTodo(taskUpdate,checkCompleted,id))
    
    //setUpdate('')
    //setTaskUpdate('')
  }

const handleChange = (e)=>{
 

  //setTaskUpdate({...taskUpdate,[e.target.id]:e.target.value })
}
 

const check = (e,id)=>{
  const checkValue = e.target.checked;
  dispatch(updateCheck(id,checkValue))
 // setCheckCompleted(e.target.checked)
  //dispatch(updateTodo(taskUpdate,checkCompleted,id))
    

}
const { title,task,isCompleted } = taskUpdate;

  console.log(listTask)
  const displayTask = listTask.map(todo=>(
    <tr key={todo.id} className={ todo.isCompleted && `isCompleted`}>
      <td>
        <input type="checkbox"  onChange={(e)=>check(e,todo.id)} checked={isCompleted} />
      </td>

      {
        update == todo.id
        ?
        (

          <>

          <td>
            <input 
            id="title"
            type='text' 
            value={title}  
            onChange={handleChange}
            />
          </td>
          <td>
            <input 
            id="task"
            type='text' 
            value={task}  
            onChange={handleChange}
            />
          </td>
          </>
        )
        :
        (
          <>
            <td>{todo.title}</td>
          <td>{todo.task}</td>
          </>
        )
      }
      <td>
      {
        update == todo.id
        ? 
        (<button onClick={()=>{hanldeUpdate(todo.id)}}>
          Save
        </button>)
        :
        (
          !todo.isCompleted && <button onClick={()=>showInputUpdate(todo.id,todo.task,todo.title,todo.isCompleted)}>
          Update
        </button>
        )
      }
      </td>
      <td>
        <button onClick={()=>dispatch(deleteTodo(todo.id))}>
          Delete
        </button>
      </td>
    </tr>
  ))
  return (
    <div>
      <hr />
      <h3>
        <u>List Task</u>
      </h3>
      <div id='mainTable'>
        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Title</th>
              <th>Todo</th>
              <th>Update</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
          {displayTask}
          </tbody>
        </table>
        
      
      </div>
    </div>
  )
}

export default TodoList
