import React,{useState} from 'react'
import { TodoForm } from '../TodoEditor'






const TodoList = ({todos}) => {
    const [idTaskUpdate, setIdTaskUpdate] = useState('')
    const [goUpdate, setGoUpdate] = useState(false)

    const showFormUpdate = (id)=>{
        setIdTaskUpdate(id)
    }
    const task = {
        title: '',
        description: ''
    }

    const displayTodo = todos.map((todo,index)=>(
        index == idTaskUpdate ? <TodoForm task={task}  /> : (
            <tr key={index} onClick={()=>showFormUpdate(index)}>
            <td>
                <input type="checkbox" name="" id=""/>
            </td>
            <td>
                {todo.title}
            </td>
            <td>
                {todo.description}
            </td>
            <td>
                <button>
                    delete
                </button>
            </td>
        </tr>
        )
    ))
    return (
        <div>
           <TodoForm  />
            <table>
                <thead>
                    <tr>
                        <th>Check</th>
                        <th>Title</th>
                        <th>Todo</th>
                        <th>Del</th>
                    </tr>
                </thead>
                <tbody>
                    {displayTodo}
                </tbody>
            </table>
        </div>
    )
}

export default TodoList
