import React,{useState} from 'react'
import { connect } from 'react-redux'
import { addTodo } from '../../redux'
import ITodo from '../../ITodo';
import TextField from '@material-ui/core/TextField'

const TodoEditor = (props) => {
  const data = {
    title: ITodo.title,
    description: ITodo.description
  }
  const {listTask,addTodo} = props;
  const [todo, setTodo] = useState(data)
  const handleChange = e=>{
    setTodo({...todo,[e.target.id]: e.target.value})
  }

  const handleSubmit = (e)=>{

    const { title,description } = todo
    e.preventDefault();
    if(title && description){
      addTodo(todo);
    }else{
      alert("champ vide")
    }
    
     setTodo(data);
  }
  const { title,description } = todo
  return (
    <div>


        <span>
        <TextField 
          label="Title" 
          variant="outlined" 
          value="Title"
          id="title"
          type="text" 
          value={title || ''} 
          onChange={handleChange}
          />
        </span>

        <span>
        <TextField 
          label="Description" 
          variant="outlined" 
          id="description"
          type="text" 
          value={description || ''} 
          placeholder="Qu'est ce que vous voulez faire ? " 
          onChange={handleChange}
          />
        </span>


     
      
      
      <button id="bAdd" onClick={handleSubmit}>ENregistrer</button>
      <button  className="end" onClick={props.handleModal}>Listes des taches términés </button>
    </div>
  )
}

const mapStateToProps = state=>{
  return{
    listTask: state.listTask
  }
}
const mapDispatchToProps = dispatch=>{
  return{
    addTodo: (todo)=>dispatch(addTodo(todo))
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(TodoEditor)
